SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_topografia_lib (nr_seq_topografia_p bigint) RETURNS varchar AS $body$
DECLARE




ds_retorno_w 		 varchar(1) := 'S';

qt_reg_w	 		 bigint;

cd_estabelecimento_w bigint;

cd_empresa_w		 bigint;




BEGIN



if (nr_seq_topografia_p > 0) then



	select 	count(*)

	into STRICT	qt_reg_w

	from	topografia_dor_liberacao

	where 	nr_seq_topografia = nr_seq_topografia_p

	and 	coalesce(ie_situacao,'A') = 'A';



	if (qt_reg_w > 0)  then

	

		cd_estabelecimento_w := obter_estabelecimento_ativo;
		
		cd_empresa_w := obter_empresa_estab(cd_estabelecimento_w);

		select 	coalesce(max('S'),'N')

		into STRICT	ds_retorno_w

		from	topografia_dor_liberacao

		where 	nr_seq_topografia = nr_seq_topografia_p

		and 	coalesce(cd_estabelecimento,cd_estabelecimento_w) = cd_estabelecimento_w
		
		and		coalesce(cd_empresa,cd_empresa_w) = cd_empresa_w

		and 	coalesce(ie_situacao,'A') = 'A'
		
		and (coalesce(ie_visualizar::text, '') = '' or ie_visualizar = 'S');

	

	end if;	

	

end if;



return ds_retorno_w;



end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
