SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_totais_cobranca (nr_seq_cobranca_p bigint, ie_tipo_dado_p text) RETURNS bigint AS $body$
DECLARE

/*
VNB = vl_nao_baixado
VBP = vl_baixa_parcial
QNB = qt_nao_baixado
QBP = qt_baixa_parcial
VL =   vl_liquidado
QNC = qt_nota_credito
VTL = vl_titulo_log
QTL = qt_titulo_liq
QNTL = qt_nao_tit_liq

*/
ds_retorno_w  		double precision;
vl_nao_baixado_w  	double precision;
vl_baixa_parcial_w  	double precision;
qt_nao_baixado_w	double precision;
qt_baixa_parcial_w	double precision;
vl_liquidado_w		double precision;
qt_nota_credito_w	double precision;
vl_titulo_log_w		double precision;
qt_titulo_liq_w		double precision;
qt_nao_tit_liq_w	double precision;


BEGIN


IF (ie_tipo_dado_p = 'VNB') then

	select	coalesce(sum(coalesce(vl_nao_baixado,0)),0)
	into STRICT 	vl_nao_baixado_w
	from (SELECT (coalesce(x.vl_saldo_inclusao,0) - coalesce(x.vl_liquidacao,0) - coalesce(x.vl_desconto,0)) vl_nao_baixado
		FROM titulo_receber_cobr x
LEFT OUTER JOIN banco_ocorr_escrit_ret y ON (x.nr_seq_ocorrencia_ret = y.nr_sequencia)
WHERE x.nr_seq_cobranca	= nr_seq_cobranca_p  and coalesce(y.ie_rejeitado, 'X') <> 'L' and coalesce(y.ie_situacao, 'A') = 'A' and coalesce(x.vl_saldo_inclusao,0) > 0 ) alias11;

		ds_retorno_w:= vl_nao_baixado_w;

ELSIF (ie_tipo_dado_p = 'VBP') THEN

	select	coalesce(sum(coalesce(vl_baixa_parcial,0)),0)
	into STRICT vl_baixa_parcial_w
from (SELECT	coalesce(x.vl_liquidacao,0) vl_baixa_parcial
		from	titulo_receber_cobr x
		where	x.nr_seq_cobranca	= nr_seq_cobranca_p
		and	CASE WHEN coalesce(x.vl_saldo_inclusao,0)=0 THEN 0  ELSE (coalesce(x.vl_saldo_inclusao,0) - coalesce(x.vl_liquidacao,0) - coalesce(x.vl_desconto,0)) END  > 0) alias10;

		ds_retorno_w:= vl_baixa_parcial_w;

ELSIF (ie_tipo_dado_p = 'QNB') THEN


	select	coalesce(sum(coalesce(qt_nao_baixado,0)),0)
	into STRICT qt_nao_baixado_w
from (SELECT	1 qt_nao_baixado
		FROM titulo_receber_cobr x
LEFT OUTER JOIN banco_ocorr_escrit_ret y ON (x.nr_seq_ocorrencia_ret = y.nr_sequencia)
WHERE x.nr_seq_cobranca	= nr_seq_cobranca_p  and coalesce(y.ie_rejeitado, 'X') <> 'L' and coalesce(y.ie_situacao, 'A') = 'A' and coalesce(x.vl_saldo_inclusao,0) > 0 ) alias7;

		ds_retorno_w:=	qt_nao_baixado_w;

ELSIF (ie_tipo_dado_p = 'QBP') THEN

	select	coalesce(sum(coalesce(qt_baixa_parcial,0)),0)
	into STRICT qt_baixa_parcial_w
from (SELECT	1 qt_baixa_parcial
		from	titulo_receber_cobr x
		where	x.nr_seq_cobranca	= nr_seq_cobranca_p
		and	CASE WHEN coalesce(x.vl_saldo_inclusao,0)=0 THEN 0  ELSE (coalesce(x.vl_saldo_inclusao,0) - coalesce(x.vl_liquidacao,0) - coalesce(x.vl_desconto,0)) END  > 0) alias9;

		ds_retorno_w:= qt_baixa_parcial_w;

ELSIF (ie_tipo_dado_p = 'VL') THEN

	select	coalesce(sum(coalesce(vl_liquidado,0)),0)
	into STRICT vl_liquidado_w
from (SELECT	coalesce(x.vl_cobranca,0) vl_liquidado
		from	banco_ocorr_escrit_ret y,
			titulo_receber_cobr x
		where	x.nr_seq_cobranca	= nr_seq_cobranca_p
		and	x.nr_seq_ocorrencia_ret	= y.nr_sequencia
		and	y.ie_rejeitado = 'L') alias5;

		ds_retorno_w:= vl_liquidado_w;

ELSIF (ie_tipo_dado_p = 'QNC') THEN
	select	count(1)
	into STRICT qt_nota_credito_w
		from	titulo_receber_cobr	b
		where	b.nr_seq_cobranca	= nr_seq_cobranca_p
		and	substr(obter_dados_titulo_rec_cobr(b.nr_sequencia,'NC'),1,255) is not null;

		ds_retorno_w:= qt_nota_credito_w;

ELSIF (ie_tipo_dado_p = 'VTL') THEN

	select   coalesce(sum(coalesce(vl_saldo_titulo,0)),0)
	into STRICT vl_titulo_log_w
		from     cobranca_escrit_log
		where    nr_seq_cobranca = nr_seq_cobranca_p
		order by 1;

		ds_retorno_w:= vl_titulo_log_w;

ELSIF (ie_tipo_dado_p = 'QTL') THEN


	select 	count(1)
	into STRICT qt_titulo_liq_w
		from	titulo_receber		b,
			titulo_receber_cobr 	a
		where 	a.nr_titulo		= b.nr_titulo
		and	a.nr_seq_cobranca	= nr_seq_cobranca_p
		and	b.ie_situacao		in ('2');

		ds_retorno_w:= qt_titulo_liq_w;

ELSIF (ie_tipo_dado_p = 'QNTL') THEN

	select 	count(1)
	into STRICT	qt_nao_tit_liq_w
		from	titulo_receber		b,
			titulo_receber_cobr 	a
		where 	a.nr_titulo		= b.nr_titulo
		and	a.nr_seq_cobranca	= nr_seq_cobranca_p
		and	b.ie_situacao		not in ('2');

	ds_retorno_w:= qt_nao_tit_liq_w;
end if;
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_totais_cobranca (nr_seq_cobranca_p bigint, ie_tipo_dado_p text) FROM PUBLIC;

