CREATE OR REPLACE FUNCTION obter_totais_nf_conta_pagador ( nr_interno_conta_p bigint, ie_opcao_p bigint) RETURNS bigint AS $body$
DECLARE


/*ie_opcao_p
0 - VL_MERCADORIA
1 - VL_TOTAL_NOTA*/
vl_mercadoria_w			double precision;
vl_total_nota_w			double precision;
vl_retorno_w			double precision;


BEGIN

select	coalesce(sum(vl_mercadoria),0),
	coalesce(sum(vl_total_nota),0)
into STRICT	vl_mercadoria_w,
	vl_total_nota_w
from	nota_fiscal
where	(nr_seq_nf_pagador IS NOT NULL AND nr_seq_nf_pagador::text <> '')
and	nr_interno_conta = nr_interno_conta_p
and	(dt_atualizacao_estoque IS NOT NULL AND dt_atualizacao_estoque::text <> '')
and	ie_situacao = '1';

if (ie_opcao_p = 0) then
	vl_retorno_w	:= vl_mercadoria_w;
elsif (ie_opcao_p = 1) then
	vl_retorno_w	:= vl_total_nota_w;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
