SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_total_alerta_api ( nr_prescricao_p bigint, cd_api_p text) RETURNS bigint AS $body$
DECLARE


ds_retorno_w	bigint;


BEGIN
	if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
		select 	count(nr_sequencia)
		into STRICT 	ds_retorno_w
		from 	alerta_api 
		where 	nr_prescricao 	= nr_prescricao_p 
		and 	cd_api 	= cd_api_p
		and     coalesce(ie_status_requisicao::text, '') = '';
	end if;

	return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
