SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_total_aplicacao (nr_seq_aplicacao_p bigint) RETURNS bigint AS $body$
DECLARE


vl_aplicacao_estrang_w			banco_aplicacao.vl_aplicacao_estrang%type := 0;
vl_aplicacao_w					banco_aplicacao.vl_aplicacao%type := 0;
vl_total_resgate_w				banco_resgate.vl_resgate%type := 0;
vl_total_resgate_estrang_w		banco_resgate.vl_resgate_estrang%type := 0;
vl_total_rendimento_w			banco_rendimento.vl_rendimento%type := 0;
vl_total_rendimento_estrang_w	banco_rendimento.vl_rendimento_estrang%type := 0;
vl_total_deposito_w             banco_aplic_deposito.vl_deposito%type := 0;
vl_total_deposito_estrang_w     banco_aplic_deposito.vl_deposito_estrang%type := 0;
vl_total_w						banco_resgate.vl_total_aplic%type := 0;


BEGIN

if (nr_seq_aplicacao_p IS NOT NULL AND nr_seq_aplicacao_p::text <> '') then
	begin
	select	coalesce(a.vl_aplicacao_estrang,0) vl_aplicacao_estrang,
			coalesce(a.vl_aplicacao,0) vl_aplicacao
	into STRICT	vl_aplicacao_estrang_w,
			vl_aplicacao_w
	from	banco_aplicacao a
	where	a.nr_sequencia = nr_seq_aplicacao_p;
	exception when others then
	vl_aplicacao_estrang_w := 0;
	vl_aplicacao_w := 0;
	end;
	
	begin
	select	coalesce(sum(c.vl_resgate),0)vl_total_resgate,
			coalesce(sum(c.vl_resgate_estrang),0) vl_total_resgate_estrang
	into STRICT	vl_total_resgate_w,
			vl_total_resgate_estrang_w
	FROM banco_aplicacao a
LEFT OUTER JOIN banco_resgate c ON (a.nr_sequencia = c.nr_seq_aplicacao)
WHERE a.nr_sequencia = nr_seq_aplicacao_p;
	exception when others then
	vl_total_resgate_w := 0;
	vl_total_resgate_estrang_w := 0;
	end;

	begin
	select	coalesce(sum(b.vl_rendimento),0) vl_total_rendimento,
			coalesce(sum(b.vl_rendimento_estrang),0) vl_total_rendimento_estrang
	into STRICT	vl_total_rendimento_w,
			vl_total_rendimento_estrang_w
	FROM banco_aplicacao a
LEFT OUTER JOIN banco_rendimento b ON (a.nr_sequencia = b.nr_seq_aplicacao)
WHERE a.nr_sequencia = nr_seq_aplicacao_p;
	exception when others then
	vl_total_rendimento_w := 0;
	vl_total_rendimento_estrang_w := 0;
	end;	
	
	begin
    select	coalesce(sum(b.vl_deposito),0) vl_total_deposito,
			coalesce(sum(b.vl_deposito_estrang),0) vl_total_deposito_estrang
	into STRICT	vl_total_deposito_w,
			vl_total_deposito_estrang_w
	FROM banco_aplicacao a
LEFT OUTER JOIN banco_aplic_deposito b ON (a.nr_sequencia = b.nr_seq_aplicacao)
WHERE a.nr_sequencia = nr_seq_aplicacao_p;
    
    exception when others then
	vl_total_deposito_w := 0;
	vl_total_deposito_estrang_w := 0;
    end;
	
	if (vl_aplicacao_estrang_w = 0)then
		vl_total_w := vl_aplicacao_w + vl_total_rendimento_w + vl_total_deposito_w - vl_total_resgate_w;
	else
		vl_total_w := vl_aplicacao_estrang_w + vl_total_rendimento_estrang_w + vl_total_deposito_estrang_w - vl_total_resgate_estrang_w;
	end if;

	
end if;

return vl_total_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
