SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_total_aplic_gas (nr_seq_gasoterapia_p bigint, ie_dado_p text) RETURNS varchar AS $body$
DECLARE


/*
Tipo de dado:
	T = Tempo Total
	Q = Quantidade Total
*/
ds_retorno_w		varchar(80);
ie_evento_w		varchar(15);
ie_somar_w		varchar(1);
qt_minutos_w		bigint := 0;
dt_evento_w		timestamp;
dt_inicio_w		timestamp;

c01 CURSOR FOR
SELECT	dt_evento,
	ie_evento
from	prescr_gasoterapia_evento
where	nr_seq_gasoterapia	= nr_seq_gasoterapia_p
and	coalesce(ie_evento_valido,'S') = 'S'
--and	b.ie_evento 		<> 'I'
order by dt_evento;


BEGIN

if (ie_dado_p = 'Q') then

	select	to_char(sum(coalesce(qt_gasoterapia,0)))
	into STRICT	ds_retorno_w
	from	prescr_gasoterapia_evento
	where	nr_seq_gasoterapia = nr_seq_gasoterapia_p
	and	coalesce(ie_evento_valido,'S') = 'S';

elsif (ie_dado_p = 'T') then

	open C01;
	loop
	fetch C01 into
		dt_evento_w,
		ie_evento_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */

		if (ie_evento_w	in ('R','I')) then
			dt_inicio_w	:= dt_evento_w;
			ie_somar_w	:= 'S';
		elsif (ie_evento_w	in ('IN','T','TE')) and (ie_somar_w	= 'S') then
			qt_minutos_w	:= qt_minutos_w + obter_min_entre_datas(dt_inicio_w, dt_evento_w, 1);
			ie_somar_w	:= 'N';
		end if;

	end loop;
	close C01;

	select	obter_horas_minutos_segundos(qt_minutos_w*60)
	into STRICT	ds_retorno_w
	;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
