CREATE OR REPLACE FUNCTION obter_total_atendimento ( nr_atendimento_p bigint, ie_opcao_p integer) RETURNS bigint AS $body$
DECLARE

 
C01 CURSOR FOR 
	SELECT	nr_interno_conta 
	from	conta_paciente 
	where	nr_atendimento		= nr_atendimento_p;
	
nr_interno_conta_w		bigint;
vl_retorno_w			double precision :=0;

BEGIN
 
 
open C01;
loop 
fetch C01 into	 
	nr_interno_conta_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	vl_retorno_w	:= vl_retorno_w + obter_valor_conta(nr_interno_conta_w,ie_opcao_p);
	end;
end loop;
close C01;
 
return	vl_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
