CREATE OR REPLACE FUNCTION obter_total_ciclos (nr_prescricao_p bigint) RETURNS bigint AS $body$
DECLARE


nr_total_ciclos_w	bigint := 0;


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then

	select	max(trunc(CASE WHEN(coalesce(qt_hora_duracao,0) * 60) + coalesce(qt_min_duracao,0)=0 THEN 0  ELSE ((coalesce(qt_hora_sessao,0) * 60) + coalesce(qt_min_sessao,0)) / ((coalesce(qt_hora_duracao,0) * 60) + coalesce(qt_min_duracao,0)) END ))
	into STRICT 	nr_total_ciclos_w
	from 	hd_prescricao
	where 	nr_prescricao = nr_prescricao_p;

	if (coalesce(nr_total_ciclos_w,0) = 0) then
		nr_total_ciclos_w	:= 0;
	end if;

end if;

return nr_total_ciclos_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
