CREATE OR REPLACE FUNCTION obter_total_cirurgia (dt_inicial_p timestamp, dt_final_p timestamp) RETURNS bigint AS $body$
DECLARE

 
nr_cirurgias_w		bigint;


BEGIN 
 
SELECT	count(distinct a.nr_cirurgia) 
into STRICT	nr_cirurgias_w 
FROM medico m, estrutura_procedimento_v g, procedimento e, pessoa_fisica p
LEFT OUTER JOIN compl_pessoa_fisica f ON (p.cd_pessoa_fisica = f.cd_pessoa_fisica)
, cirurgia a
LEFT OUTER JOIN atendimento_paciente c ON (a.nr_atendimento = c.nr_atendimento)
LEFT OUTER JOIN atend_paciente_unidade b ON (a.nr_atendimento = b.nr_atendimento AND a.dt_entrada_unidade = b.dt_entrada_unidade)
LEFT OUTER JOIN agenda_paciente h ON (a.nr_cirurgia = h.nr_cirurgia)
LEFT OUTER JOIN medico an ON (a.cd_medico_anestesista = an.cd_pessoa_fisica)
LEFT OUTER JOIN procedencia q ON (h.cd_procedencia = q.cd_procedencia)
, obter_atecaco_atendimento(a
LEFT OUTER JOIN atend_categoria_convenio acc ON (obter_atecaco_atendimento(a.nr_atendimento) = acc.nr_seq_interno)
WHERE ((a.dt_inicio_real BETWEEN dt_inicial_p AND dt_final_p AND 0 = 0) 
OR   ((coalesce(a.dt_inicio_real,a.dt_inicio_prevista) BETWEEN dt_inicial_p AND dt_final_p) AND (0 = 1)) 
OR   ((c.dt_alta BETWEEN dt_inicial_p AND fim_dia(dt_final_p)) AND (0 = 2))) AND a.cd_procedimento_princ = 	e.cd_procedimento AND a.ie_origem_proced     = 	e.ie_origem_proced     AND a.cd_pessoa_fisica	   = 	p.cd_pessoa_fisica AND g.cd_procedimento = e.cd_procedimento AND g.ie_origem_proced = e.ie_origem_proced AND m.cd_pessoa_fisica = a.cd_medico_cirurgiao     and ((a.dt_termino IS NOT NULL AND a.dt_termino::text <> '') or (a.ie_status_cirurgia = '3')) order by a.dt_inicio_real;
 
 
return	nr_cirurgias_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
