CREATE OR REPLACE FUNCTION obter_total_custo_real ( nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE


ie_considerar_pessoas_w	varchar(1);
total_custo_real_w	double precision;


BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	begin
	ie_considerar_pessoas_w := obter_valor_param_usuario(912, 12, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento);
	select	CASE WHEN ie_considerar_pessoas_w='S' THEN (sum(o.vl_custo_real /* * d.qt_pessoa_atend */
 ))  ELSE sum(o.vl_custo_real) END  total_custo_real
	into STRICT	total_custo_real_w
	from    nut_rec_real_comp o,
        	nut_receita_real r,
        	nut_cardapio c,
        	nut_servico s,
        	nut_cardapio_dia d
	where	o.nr_seq_rec_real 	= r.nr_sequencia
	and     r.nr_seq_cardapio    	= c.nr_sequencia
	and     c.nr_seq_card_dia	= d.nr_sequencia
	and     s.nr_sequencia   	= d.nr_seq_servico
	and     d.nr_sequencia		= nr_sequencia_p;
	end;
end if;
return	total_custo_real_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
