CREATE OR REPLACE FUNCTION obter_total_diluicao_medic ( nr_prescricao_p bigint, nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE

					
qt_solucao_w	double precision;
qt_reg_w	bigint;
qt_rediluente_w	double precision;
qt_diluente_w	double precision;
qt_dose_ml_w	double precision;
ie_medic_possui_conv_ml_w	varchar(1);

BEGIN
select	max(qt_solucao),
	coalesce(max(obter_conversao_ml(a.cd_material, a.qt_dose, a.cd_unidade_medida_dose)),0)
into STRICT	qt_solucao_w,
	qt_dose_ml_w
from	prescr_material a
where	nr_prescricao	= nr_prescricao_p
and	nr_sequencia	= nr_sequencia_p;

select	coalesce(max('S'),'N')
into STRICT	ie_medic_possui_conv_ml_w
from	material_conversao_unidade
where	cd_unidade_medida = obter_unid_med_usua('ml')
and	cd_material = (SELECT max(cd_material)
    from	prescr_material
    where	nr_prescricao	= nr_prescricao_p
    and	nr_sequencia	= nr_sequencia_p
    and	ie_agrupador	= 1);

select count(*)
into STRICT	qt_reg_w
from	prescr_material
where	nr_prescricao	= nr_prescricao_p
and	nr_sequencia_diluicao	= nr_sequencia_p
and	ie_agrupador	= 9;

if (ie_medic_possui_conv_ml_w = 'N' and qt_reg_w > 0)	then
	select	obter_conversao_ml(cd_material,qt_dose,cd_unidade_medida_dose) + coalesce(qt_solucao,0) + qt_dose_ml_w
	into STRICT	qt_dose_ml_w
	from	prescr_material
	where	nr_prescricao	= nr_prescricao_p
	and	nr_sequencia_diluicao	= nr_sequencia_p
	and	ie_agrupador	= 9;
end if;

if (coalesce(qt_solucao_w,0)	> 0) then
	return	 qt_solucao_w;
end if;

select	count(*)
into STRICT	qt_reg_w
from	prescr_material
where	nr_prescricao	= nr_prescricao_p
and	nr_sequencia_diluicao	= nr_sequencia_p
and	ie_agrupador		= 7;

if (qt_reg_w	> 0) then
	select	obter_conversao_ml(cd_material,qt_dose,cd_unidade_medida_dose) + coalesce(qt_solucao,0) + qt_dose_ml_w
	into STRICT	qt_rediluente_w
	from	prescr_material
	where	nr_prescricao	= nr_prescricao_p
	and	nr_sequencia_diluicao	= nr_sequencia_p
	and	ie_agrupador		= 7;
	return	 qt_rediluente_w;
end if;

select	count(*)
into STRICT	qt_reg_w
from	prescr_material
where	nr_prescricao	= nr_prescricao_p
and	nr_sequencia_diluicao	= nr_sequencia_p
and	ie_agrupador		= 3;

if (qt_reg_w	> 0) then
	select	obter_conversao_ml(cd_material,qt_dose,cd_unidade_medida_dose) + coalesce(qt_solucao,0) + qt_dose_ml_w
	into STRICT	qt_diluente_w
	from	prescr_material
	where	nr_prescricao	= nr_prescricao_p
	and	nr_sequencia_diluicao	= nr_sequencia_p
	and	ie_agrupador		= 3;
	return	 qt_diluente_w;
end if;

if (qt_dose_ml_w > 0) then
	return	qt_dose_ml_w;
end if;

return 0;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
