CREATE OR REPLACE FUNCTION obter_total_episodios_ih_niss ( cd_setor_atendimento_p bigint, nr_atendimento_p bigint, dt_referencia_p timestamp, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


ie_exige_infec_disp_w	varchar(1);
qt_ih_w			bigint;


BEGIN

select	coalesce(max(ie_exige_infec_disp),'N')
into STRICT	ie_exige_infec_disp_w
from	parametro_medico
where	cd_estabelecimento = cd_estabelecimento_p;

if (ie_exige_infec_disp_w = 'S') then
	select	coalesce(sum(1),0)
	into STRICT	qt_ih_w
	from	qua_evento c,
		qua_evento_paciente b,
		atend_pac_dispositivo a
	where	dt_evento between trunc(dt_referencia_p,'dd') and fim_dia(dt_referencia_p)
	and	b.nr_seq_disp_pac				= a.nr_sequencia
	and	b.nr_seq_evento					= c.nr_sequencia
	and	c.ie_infeccao					= 'S'
	and	a.nr_atendimento 				= nr_atendimento_p
	and	coalesce(b.ie_tipo_evento,'E') = 'E'
	and	obter_dados_dispositivo(a.nr_sequencia,'CS') 	= cd_setor_atendimento_p;
else
	select	coalesce(sum(1),0)
	into STRICT	qt_ih_w
	from	niss_sitio_especifico c,
		cih_local_infeccao a,
		cih_ficha_ocorrencia b
	where	a.dt_infeccao between trunc(dt_referencia_p,'dd') and fim_dia(dt_referencia_p)
	and	a.nr_ficha_ocorrencia				=	b.nr_ficha_ocorrencia
	and	a.nr_seq_sitio_espec 				= 	c.nr_sequencia
	and	c.cd_sitio_especifico in ('LCBI','CSEP','PNEU','SUTI','ASB')
	and	cih_obter_tipo_infeccao(a.cd_caso_infeccao) not in ('2')
	and	campo_numerico(cih_obter_setor_infeccao_niss(a.nr_ficha_ocorrencia, a.dt_infeccao,'C')) = cd_setor_atendimento_p
	and	b.nr_atendimento 				= nr_atendimento_p;
end if;

return	qt_ih_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
