CREATE OR REPLACE FUNCTION obter_total_horas_rat ( nr_sequencia_p bigint , ie_funcao_exec_p text, rat_aprovada_p text default 'N') RETURNS bigint AS $body$
DECLARE


qt_total_hora_w bigint;


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '' AND ie_funcao_exec_p IS NOT NULL AND ie_funcao_exec_p::text <> '') then

	select	sum(d.qt_total_hora)
	into STRICT	qt_total_hora_w
	from	proj_rat d
	where coalesce(d.ie_funcao_exec,'CONSULT') in (WITH RECURSIVE cte AS (
SELECT coalesce(trim(both regexp_substr(ie_funcao_exec_p,'[^,]+', 1, level)), ie_funcao_exec_p)

                                (regexp_substr(ie_funcao_exec_p, '[^,]+', 1, level) IS NOT NULL AND (regexp_substr(ie_funcao_exec_p, '[^,]+', 1, level))::text <> '')  UNION ALL
SELECT coalesce(trim(both regexp_substr(ie_funcao_exec_p,'[^,]+', 1, level)), ie_funcao_exec_p) JOIN cte c ON ()

) SELECT * FROM cte;
)
	and	d.nr_seq_proj = nr_sequencia_p
    and (((d.dt_aprovacao IS NOT NULL AND d.dt_aprovacao::text <> '') and rat_aprovada_p = 'S') or rat_aprovada_p <> 'S' );
end if;

if (coalesce(qt_total_hora_w::text, '') = '') then
qt_total_hora_w := 0;
end if;

return qt_total_hora_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
