SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_total_hora_cronograma ( nr_seq_projeto_p text, nr_seq_cliente_p text, ie_tipo_valor_p text) RETURNS bigint AS $body$
DECLARE


nr_total_retorno_w	bigint;
nr_total_previsto	bigint;
nr_total_saldo		bigint;
nr_total_real		bigint;
	

BEGIN

select	sum(qt_total_horas) previsto,
	sum(qt_horas_realizado) real,
	sum(qt_horas_saldo) saldo
into STRICT	nr_total_previsto,
	nr_total_real,
	nr_total_saldo
from	proj_cronograma
where	nr_seq_cliente = nr_seq_cliente_p
and	nr_seq_proj = nr_seq_projeto_p
and	coalesce(ie_cliente, 'N') = 'N';

if (ie_tipo_valor_p = 'S') then
	nr_total_retorno_w:= nr_total_saldo;
elsif (ie_tipo_valor_p = 'P') then
	nr_total_retorno_w:= nr_total_previsto;
elsif (ie_tipo_valor_p = 'R') then
	nr_total_retorno_w:= nr_total_real;
end if;

return	nr_total_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
