CREATE OR REPLACE FUNCTION obter_total_hora_escala ( dt_inicio_p timestamp, dt_final_p timestamp, cd_pf_p text, nr_seq_p bigint) RETURNS bigint AS $body$
DECLARE


qt_hora_retorno_w double precision := 0;
dt_inicio_w  timestamp;
dt_fim_w  timestamp;

C01 CURSOR FOR

SELECT	dt_inicio,
	dt_fim
from	escala_diaria
where	cd_pessoa_fisica  = cd_pf_p
and	nr_seq_escala   = nr_seq_p
and	dt_inicio between dt_inicio_p and dt_final_p + 86399/86400;


BEGIN
open	c01;
loop
fetch	c01 into
	dt_inicio_w,
	dt_fim_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */

begin
if (dt_inicio_w >= trunc(dt_inicio_p,'dd')) and (trunc(dt_fim_w,'dd') <= dt_final_p) then
	qt_hora_retorno_w	:= qt_hora_retorno_w + (dt_fim_w - dt_inicio_w);
elsif (dt_inicio_w >= trunc(dt_inicio_p,'dd')) and (dt_fim_w > dt_final_p) then
	qt_hora_retorno_w	:= qt_hora_retorno_w + (TRUNC(dt_inicio_w + 1) - dt_inicio_w);
end if;

end;

end loop;

close c01;

RETURN abs(qt_hora_retorno_w * 24);

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
