CREATE OR REPLACE FUNCTION obter_total_hora_extra ( dt_inicial_p timestamp, dt_final_p timestamp, cd_estabelecimento_p bigint, ie_estagiario_p text) RETURNS bigint AS $body$
DECLARE


dt_inicial_w	timestamp;
dt_final_w	timestamp;
qt_dia_util_w	double precision	:= 0;


BEGIN

dt_inicial_w	:= dt_inicial_p;
dt_final_w	:= dt_final_p;

while(dt_inicial_w <= dt_final_w) loop
	begin

	if (obter_se_hora_extra(dt_inicial_w, cd_estabelecimento_p) = 'N') then
		qt_dia_util_w	:= qt_dia_util_w + 1;
	end if;
	dt_inicial_w	:= dt_inicial_w + 1;

	end;
end loop;

if (ie_estagiario_p = 'S') then
	return(qt_dia_util_w * 8);
else
	return(qt_dia_util_w * 8.8);
end	if;


end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
