SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_total_item_audit_externa (nr_sequencia_p bigint, ie_tipo_item_p text) RETURNS bigint AS $body$
DECLARE


vl_retorno_w	double precision;


BEGIN

if (ie_tipo_item_p = 'P') then

	select 	sum(coalesce(c.vl_procedimento,0))
	into STRICT	vl_retorno_w
	from 	procedimento_paciente c,
		auditoria_externa_item b,
		auditoria_externa a
	where 	a.nr_sequencia = b.nr_seq_audit_ext
	and 	b.nr_seq_item = c.nr_sequencia
	and 	a.nr_sequencia = nr_sequencia_p;


else

	select 	sum(coalesce(c.vl_material,0))
	into STRICT	vl_retorno_w
	from 	material_atend_paciente c,
		auditoria_externa_item b,
		auditoria_externa a
	where 	a.nr_sequencia = b.nr_seq_audit_ext
	and 	b.nr_seq_item = c.nr_sequencia
	and 	a.nr_sequencia = nr_sequencia_p;

end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
