CREATE OR REPLACE FUNCTION obter_total_item_kit_cir (nr_prescricao_p bigint, cd_kit_material_p bigint, nr_seq_kit_estoque_p bigint, ie_tipo_p text) RETURNS bigint AS $body$
DECLARE


quantidade_w	bigint;



BEGIN
IF (ie_tipo_p = 'M') AND (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') AND (cd_kit_material_p IS NOT NULL AND cd_kit_material_p::text <> '') THEN
	BEGIN
	SELECT  SUM(qt_material)
	INTO STRICT	quantidade_w
	FROM    PRESCR_MATERIAL a
	WHERE   1 = 1
	AND     cd_kit_material = cd_kit_material_p
	AND     nr_prescricao = nr_prescricao_p
	AND	ie_tipo_p = 'M';

	END;

END IF;

IF (ie_tipo_p = 'E') AND (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') AND (nr_seq_kit_estoque_p IS NOT NULL AND nr_seq_kit_estoque_p::text <> '') THEN
	BEGIN
	SELECT  SUM(qt_material)
	INTO STRICT 	quantidade_w
	FROM    PRESCR_MATERIAL a
	WHERE   1 = 1
	AND     nr_seq_kit_estoque = nr_seq_kit_estoque_p
	AND     nr_prescricao = nr_prescricao_p
	AND	ie_tipo_p = 'E';

	END;
END IF;


IF (ie_tipo_p = 'N') AND (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') THEN
	BEGIN
	SELECT   SUM(qt_material)
	INTO STRICT    quantidade_w
	FROM    PRESCR_MATERIAL a
	WHERE 	1 = 1
	AND	coalesce(nr_seq_kit_estoque::text, '') = ''
	AND     coalesce(cd_kit_material::text, '') = ''
	AND	nr_prescricao = nr_prescricao_p
	AND	ie_tipo_p = 'N';

	END;
END IF;

RETURN	quantidade_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
