CREATE OR REPLACE FUNCTION obter_total_lote_protocolo (nr_seq_lote_protocolo_p bigint) RETURNS bigint AS $body$
DECLARE

 
vl_total_lote_w 		double precision;
cd_convenio_w			integer;
ie_tipo_protocolo_w		smallint;	
nr_seq_protocolo_w		bigint;		
 
C01 CURSOR FOR 
	SELECT	nr_seq_protocolo 
	from	protocolo_convenio 
	where	nr_seq_lote_protocolo = nr_seq_lote_protocolo_p 
	and 	cd_convenio = cd_convenio_w 
	and 	((coalesce(ie_tipo_protocolo_w::text, '') = '') or (ie_tipo_protocolo = ie_tipo_protocolo_w)) 
	order by 1;
			

BEGIN 
 
select 	cd_convenio, 
	ie_tipo_protocolo 
into STRICT	cd_convenio_w, 
	ie_tipo_protocolo_w 
from 	lote_protocolo 
where 	nr_sequencia = nr_seq_lote_protocolo_p;
 
vl_total_lote_w:= 0;
 
open C01;
loop 
fetch C01 into	 
	nr_seq_protocolo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	vl_total_lote_w:= vl_total_lote_w + coalesce(obter_total_protocolo(nr_seq_protocolo_w),0);	
	end;
end loop;
close C01;
 
return	vl_total_lote_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
