CREATE OR REPLACE FUNCTION obter_total_mat_dia (dt_dia_mat_p timestamp, nr_interno_conta_p bigint, cd_material_p bigint) RETURNS bigint AS $body$
DECLARE


vl_material_w           double precision;
dt_inicio_dia_w         timestamp;
dt_fim_dia_w            timestamp;


BEGIN

dt_inicio_dia_w         := trunc(dt_dia_mat_p, 'dd');
dt_fim_dia_w            := fim_dia(dt_dia_mat_p);

select  coalesce(sum(vl_material), 0)
into STRICT    vl_material_w
from    material_atend_paciente
where   nr_interno_conta        = nr_interno_conta_p
and     cd_material             = cd_material_p
and     coalesce(cd_motivo_exc_conta::text, '') = ''
and     dt_atendimento between dt_inicio_dia_w and dt_fim_dia_w;

return  vl_material_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
