CREATE OR REPLACE FUNCTION obter_total_nf_conta_pac_pag ( nr_seq_nf_pagador_p bigint, ie_opcao_p bigint) RETURNS bigint AS $body$
DECLARE


/*ie_opcao_p
0 - VL_MERCADORIA
1 - VL_TOTAL_NOTA
2 - NR_SEQUENCIA*/
vl_mercadoria_w			double precision;
vl_total_nota_w			double precision;
nr_sequencia_w			bigint;
vl_retorno_w			double precision;


BEGIN

select	coalesce(sum(vl_mercadoria),0),
	coalesce(sum(vl_total_nota),0),
	max(nr_Sequencia)
into STRICT	vl_mercadoria_w,
	vl_total_nota_w,
	nr_sequencia_w
from	nota_fiscal
where	nr_seq_nf_pagador = nr_seq_nf_pagador_p
and	ie_situacao = '1';

if (ie_opcao_p = 0) then
	vl_retorno_w	:= vl_mercadoria_w;
elsif (ie_opcao_p = 1) then
	vl_retorno_w	:= vl_total_nota_w;
elsif (ie_opcao_p = 2) then
	vl_retorno_w	:= nr_sequencia_w;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
