CREATE OR REPLACE FUNCTION obter_total_ordens_triagem (nr_seq_gerencia_p bigint, nr_seq_grupo_p bigint) RETURNS bigint AS $body$
DECLARE

qt_total_ordens_triagem_w			bigint;


BEGIN
qt_total_ordens_triagem_w := 0;
if (nr_seq_gerencia_p IS NOT NULL AND nr_seq_gerencia_p::text <> '' AND nr_seq_grupo_p IS NOT NULL AND nr_seq_grupo_p::text <> '') then
	select total
	into STRICT	qt_total_ordens_triagem_w
	from (
		SELECT	count(1)	as total,	d.nr_sequencia
		from	man_ordem_servico_v		a,
				grupo_desenvolvimento	d
		where	a.nr_seq_grupo_des	=	d.nr_sequencia
		and		d.nr_seq_gerencia	=	nr_seq_gerencia_p
		and		d.nr_sequencia		=	nr_seq_grupo_p
		and		nr_seq_estagio		=	1051
		and		a.ie_status_ordem 	<> 	3
		group by d.nr_sequencia
	) alias2;
end if;
return	qt_total_ordens_triagem_w;

end;
