CREATE OR REPLACE FUNCTION obter_total_pago_item_glosa (nr_seq_ret_glosa_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_propaci_w		bigint;
nr_seq_matpaci_w		bigint;
nr_seq_partic_w			bigint;
ds_retorno_w			double precision;


BEGIN

select	max(nr_seq_propaci),
	max(nr_seq_matpaci),
	max(nr_seq_partic)
into STRICT	nr_seq_propaci_w,
	nr_seq_matpaci_w,
	nr_seq_partic_w
from 	convenio_retorno_glosa
where	nr_sequencia	= nr_seq_ret_glosa_p;

if (nr_seq_partic_w IS NOT NULL AND nr_seq_partic_w::text <> '') then

	select	sum(vl_pago_digitado)
	into STRICT	ds_retorno_w
	from	convenio_retorno_glosa
	where	nr_seq_propaci	= nr_seq_propaci_w
	and 	nr_seq_partic	= nr_seq_partic_w;

elsif (nr_seq_propaci_w IS NOT NULL AND nr_seq_propaci_w::text <> '') then

	select	sum(vl_pago_digitado)
	into STRICT	ds_retorno_w
	from	convenio_retorno_glosa
	where	nr_seq_propaci	= nr_seq_propaci_w;

elsif (nr_seq_matpaci_w IS NOT NULL AND nr_seq_matpaci_w::text <> '') then

	select	sum(vl_pago_digitado)
	into STRICT	ds_retorno_w
	from	convenio_retorno_glosa
	where	nr_seq_matpaci	= nr_seq_matpaci_w;
end if;

return coalesce(ds_retorno_w,0);


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
