CREATE OR REPLACE FUNCTION obter_total_percentual_rateio ( nr_sequencia_p bigint, dt_vigencia_p timestamp) RETURNS bigint AS $body$
DECLARE


pr_rateio_w			double precision;


BEGIN

select	coalesce(sum(pr_rateio),0)
into STRICT	pr_rateio_w
from	ctb_criterio_rateio_item
where	nr_seq_criterio = nr_sequencia_p
and (coalesce(dt_inicio_vigencia, trunc(dt_vigencia_p)) <= trunc(dt_vigencia_p) and coalesce(dt_fim_vigencia, trunc(dt_vigencia_p)) >= trunc(dt_vigencia_p));

return pr_rateio_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
