CREATE OR REPLACE FUNCTION obter_total_proc_dia (dt_dia_proc_p timestamp, nr_interno_conta_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint) RETURNS bigint AS $body$
DECLARE


vl_procedimento_w	double precision;
dt_inicio_dia_w		timestamp;
dt_fim_dia_w		timestamp;


BEGIN

dt_inicio_dia_w		:= ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_dia_proc_p);
dt_fim_dia_w		:= fim_dia(dt_dia_proc_p);

select	/*+ index(PROPACI_CONPACI_I) */	coalesce(sum(vl_procedimento), 0)
into STRICT	vl_procedimento_w
from	procedimento_paciente
where	nr_interno_conta	= nr_interno_conta_p
and	cd_procedimento		= cd_procedimento_p
and	ie_origem_proced	= ie_origem_proced_p
and	coalesce(cd_motivo_exc_conta::text, '') = ''
and	dt_conta between dt_inicio_dia_w and dt_fim_dia_w;

return	vl_procedimento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
