CREATE OR REPLACE FUNCTION obter_total_receb_baixa (nr_seq_titulo_p bigint, nr_seq_baixa_p bigint) RETURNS bigint AS $body$
DECLARE

 
				  
vl_total_recebido_w		double precision	:= 0;
				

BEGIN 
 
if (coalesce(nr_seq_titulo_p,0) > 0) and (coalesce(nr_seq_baixa_p,0) > 0) then 
	 
	select	((coalesce(sum(b.vl_recebido),0) + coalesce(sum(b.vl_juros),0) + coalesce(sum(b.vl_multa),0) + coalesce(sum(b.vl_rec_maior),0)) + coalesce(sum(b.vl_glosa),0)) 
	into STRICT	vl_total_recebido_w 
	from	titulo_receber_liq	b, 
		titulo_receber		a 
	where	a.nr_titulo = b.nr_titulo 
	and  	coalesce(b.ie_lib_caixa, 'S') = 'S' 
	and	a.nr_titulo = nr_seq_titulo_p 
	and	b.nr_sequencia = nr_seq_baixa_p;
		 
end if;
 
return	vl_total_recebido_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
