CREATE OR REPLACE FUNCTION obter_total_terceiro_viagem (nr_seq_viagem_p bigint) RETURNS bigint AS $body$
DECLARE


vl_total_despesa_w	double precision;


BEGIN

Select	coalesce(sum(t.vl_despesa),0)
into STRICT	vl_total_despesa_w
from (
	SELECT  sum(coalesce(a.vl_despesa,0)) vl_despesa
	from    via_despesa     a,
		via_relat_desp  b
	where   a.nr_seq_relat = b.nr_sequencia
	and     b.nr_seq_viagem= nr_seq_viagem_p
	and     ie_responsavel_custo = 'T'
	
union

	SELECT sum(coalesce(obter_valor_total_hotel(nr_sequencia),0))  vl_despesa
	from    via_reserva
	where   nr_seq_viagem = nr_seq_viagem_p
	and     ie_tipo_reserva = 'H'
	and     coalesce(ie_cancelamento::text, '') = ''
	and     ie_responsavel_custo = 'T'
	and     exists (select  1
			from    via_reserva
			where   nr_seq_viagem = nr_seq_viagem_p
			and     ie_tipo_reserva = 'H'
			and     ie_responsavel_custo = 'T'
			and     coalesce(ie_cancelamento::text, '') = '')
	
union

	select  sum(coalesce(vl_reserva,0))  vl_despesa
	from    via_reserva
	where   nr_seq_viagem = nr_seq_viagem_p
	and     ie_tipo_reserva = 'T'
	and     coalesce(ie_cancelamento::text, '') = ''
	and     ie_responsavel_custo = 'T'
	and     exists (select	1
			from    via_reserva
			where   nr_seq_viagem = nr_seq_viagem_p
			and     ie_tipo_reserva = 'T'
			and     ie_responsavel_custo = 'T'
			and     coalesce(ie_cancelamento::text, '') = '')) t;

return	vl_total_despesa_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
