CREATE OR REPLACE FUNCTION obter_tot_desc_itens_oc (nr_ordem_compra_p bigint) RETURNS bigint AS $body$
DECLARE

 
 
vl_desconto_item_w		double precision;
vl_total_desconto_w		double precision := 0;

C01 CURSOR FOR 
SELECT	CASE WHEN o.vl_desconto=0 THEN (sum(o.qt_prevista_entrega * o.vl_unitario_material * o.pr_descontos) / 100)  ELSE o.vl_desconto END  vl_tot_desc	 
from	ordem_compra_v o 
where	o.nr_ordem_compra = nr_ordem_compra_p 
group by o.nr_item_oci, 
	o.vl_desconto;


BEGIN 
 
open C01;
loop 
fetch C01 into	 
	vl_desconto_item_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	vl_total_desconto_w := (vl_total_desconto_w + vl_desconto_item_w);
	end;
end loop;
close C01;
 
return	vl_total_desconto_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
