CREATE OR REPLACE FUNCTION obter_tot_despesa_viagem_con ( nr_seq_proj_p bigint, dt_aprovacao_p timestamp, ie_resp_custo_p text default 'N') RETURNS bigint AS $body$
DECLARE


vl_tot_viagens_w	double precision;


BEGIN

if (ie_resp_custo_p = 'T') then

	SELECT	SUM(coalesce(c.vl_despesa,0))
	into STRICT	vl_tot_viagens_w
	FROM	via_destino       d,
		via_despesa       c,
		via_relat_desp    b,
		via_viagem        a
	WHERE	a.nr_sequencia    = b.nr_seq_viagem
	AND	a.nr_sequencia    = d.nr_seq_viagem
	AND	b.nr_sequencia    = c.nr_seq_relat
	AND (d.nr_seq_proj     = nr_seq_proj_p)
	AND     TRUNC(b.dt_aprovacao,'month')= TRUNC(dt_aprovacao_p,'month')
	AND	c.ie_responsavel_custo = 'T';

elsif (ie_resp_custo_p = 'A') then

	SELECT	SUM(coalesce(c.vl_despesa,0))
	into STRICT	vl_tot_viagens_w
	FROM	via_destino       d,
		via_despesa       c,
		via_relat_desp    b,
		via_viagem        a
	WHERE	a.nr_sequencia    = b.nr_seq_viagem
	AND	a.nr_sequencia    = d.nr_seq_viagem
	AND	b.nr_sequencia    = c.nr_seq_relat
	AND (d.nr_seq_proj     = nr_seq_proj_p)
	AND     TRUNC(b.dt_aprovacao,'month')= TRUNC(dt_aprovacao_p,'month');

else

	SELECT	SUM(coalesce(c.vl_despesa,0))
	into STRICT	vl_tot_viagens_w
	FROM	via_destino       d,
		via_despesa       c,
		via_relat_desp    b,
		via_viagem        a
	WHERE	a.nr_sequencia    = b.nr_seq_viagem
	AND	a.nr_sequencia    = d.nr_seq_viagem
	AND	b.nr_sequencia    = c.nr_seq_relat
	AND (d.nr_seq_proj     = nr_seq_proj_p)
	AND     TRUNC(b.dt_aprovacao,'month')= TRUNC(dt_aprovacao_p,'month')
	AND	c.ie_responsavel_custo = 'N';

end if;

return vl_tot_viagens_w	;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
