CREATE OR REPLACE FUNCTION obter_tp_nfe_avista_aprazo (nr_seq_nf_p bigint) RETURNS bigint AS $body$
DECLARE


ds_retorno_w	bigint;



BEGIN

SELECT  MAX(b.ie_forma_pagamento)
INTO STRICT	ds_retorno_w
FROM   	nota_fiscal a,
	condicao_pagamento b
WHERE 	a.cd_condicao_pagamento = b.cd_condicao_pagamento
AND	a.nr_sequencia = nr_seq_nf_p;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
