SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_transf_qt_cons_barras ( nr_ordem_compra_p bigint, nr_item_oci_p bigint, ie_entrada_saida text) RETURNS bigint AS $body$
DECLARE


qt_material_w		double precision;



BEGIN

if (ie_entrada_saida = 'S') then
	select	coalesce(sum(b.qt_material),0)
	into STRICT	qt_material_w
	from	ordem_compra_item_cb b
	where	b.nr_ordem_compra 	= b.nr_ordem_compra
	and	b.nr_ordem_compra 	= nr_ordem_compra_p
	and	b.nr_item_oci     	= nr_item_oci_p
	and	coalesce(nr_seq_nota::text, '') = ''
	and	b.ie_atende_recebe	= 'A';
else
	select	coalesce(sum(b.qt_material),0)
	into STRICT	qt_material_w
	from	ordem_compra_item_cb b
	where	b.nr_ordem_compra 	= b.nr_ordem_compra
	and	b.nr_ordem_compra 	= nr_ordem_compra_p
	and	b.nr_item_oci     	= nr_item_oci_p
	and	b.ie_atende_recebe	= 'R';
end if;

return	qt_material_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
