CREATE OR REPLACE FUNCTION obter_transporte_barthel (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w 	varchar(255);


BEGIN

select 	CASE WHEN IE_TRANSPORTE=0 THEN 'Incapaz não tem equilibrio para sentar' WHEN IE_TRANSPORTE=5 THEN 'Grande ajuda (uma ou duas pessoas física) pode sentar' WHEN IE_TRANSPORTE=10 THEN 'Pequena ajuda (verbal ou física)' WHEN IE_TRANSPORTE=15 THEN 'Independente' END
into STRICT	ds_retorno_w
from	escala_barthel
where	nr_sequencia = nr_sequencia_p;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
