SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_trans_conta_deposito ( nr_seq_cheque_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_deposito_w	deposito.nr_sequencia%type;
nr_seq_conta_w		deposito.nr_conta%type;
nr_seq_trans_financ_w deposito.nr_seq_trans_financ%type;
result_w			varchar(1);


BEGIN

select	max(coalesce(b.nr_sequencia,0))
into STRICT	nr_seq_deposito_w
from    deposito b,
        deposito_cheque a
where   a.nr_seq_cheque		= nr_seq_cheque_p
and     a.nr_seq_deposito	= b.nr_sequencia;

if (nr_seq_deposito_w > 0) then
	select	max(b.nr_conta),
			max(b.nr_seq_trans_financ)
	into STRICT	nr_seq_conta_w,
			nr_seq_trans_financ_w
	from	deposito b,
		deposito_cheque a
	where	a.nr_seq_cheque		= nr_seq_cheque_p
	and	b.nr_sequencia		= nr_seq_deposito_w
	and	a.nr_seq_deposito	= b.nr_sequencia;
end if;



if (nr_seq_conta_w IS NOT NULL AND nr_seq_conta_w::text <> '') and (nr_seq_trans_financ_w IS NOT NULL AND nr_seq_trans_financ_w::text <> '') then
	result_w := 'S';
else
	result_w := 'N';
end if;

return	coalesce(result_w,'N');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
