SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_trans_fin_banco_pend (nr_seq_movto_trans_fin_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_trans_financ_w	bigint;
ds_trans_financ_w		varchar(255);
ds_retorno_w		varchar(255);


BEGIN

select	max(nr_seq_trans_financ),
	max(substr(OBTER_DESC_TRANS_FINANC(nr_seq_trans_financ),1,255))
into STRICT	nr_seq_trans_financ_w,
	ds_trans_financ_w
from	movto_trans_financ
where	nr_sequencia 	= nr_seq_movto_trans_fin_p;

if (ie_opcao_p = 'C') then
	ds_retorno_w	:= to_char(nr_seq_trans_financ_w);
elsif (ie_opcao_p = 'D') then
	ds_retorno_w	:= ds_trans_financ_w;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
