CREATE OR REPLACE FUNCTION obter_tratamento_atual (cd_pessoa_fisica_p bigint) RETURNS varchar AS $body$
DECLARE

    tratamento_w varchar(255);

BEGIN

    SELECT d.ds_valor_dominio
      INTO STRICT tratamento_w
      FROM paciente_tratamento t
     INNER JOIN valor_dominio_v d
        ON d.cd_dominio = 2197
       AND d.vl_dominio = t.ie_tratamento
     WHERE cd_pessoa_fisica = cd_pessoa_fisica_p
       AND (dt_final_tratamento > clock_timestamp() OR coalesce(dt_final_tratamento::text, '') = '')
     ORDER BY dt_inicio_tratamento DESC LIMIT 1;

    RETURN tratamento_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
