CREATE OR REPLACE FUNCTION obter_trat_odontograma ( cd_pessoa_fisica_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
ie_tratamento_w	varchar(3);

c01 CURSOR FOR
SELECT 	ie_tratamento
from	odont_procedimento
where	cd_pessoa_fisica = cd_pessoa_fisica_p
and	(ie_tratamento IS NOT NULL AND ie_tratamento::text <> '')
order by ie_tratamento;


BEGIN
ds_retorno_w := '';

open c01;
loop
fetch c01 into
	ie_tratamento_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	ds_retorno_w := ds_retorno_w || ie_tratamento_w || ',';

end loop;
close c01;

if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
	ds_retorno_w :=	substr(ds_retorno_w,1,length(ds_retorno_w)-1);
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
