SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_triagem_atendimento ( nr_atendimento_p bigint, ie_opcao_p text default 'C') RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255) := '';
nr_seq_classif_w		bigint;		
cd_pessoa_fisica_w		varchar(10);
ds_classificacao_w		varchar(60);
nm_medico_triador_w		varchar(255);
ds_funcao_w				varchar(150);
ds_registro_w			varchar(255);
nr_seq_prioridade_w		bigint;
ie_emergency_severity_score_w	triagem_classif_risco.ie_emergency_severity_score%type;

expressao1_w	varchar(255);
expressao2_w	varchar(255);
expressao3_w	varchar(255);
expressao4_w	varchar(255);
expressao5_w	varchar(255);
expressao6_w	varchar(255);



BEGIN

select	max(a.nr_seq_classif),
		max(cd_classificador),
		obter_dados_usuario_opcao(MAX(nm_usuario),'DF')
into STRICT	nr_seq_classif_w,
		cd_pessoa_fisica_w,
		ds_funcao_w
from	triagem_pronto_atend a
where	a.nr_atendimento = nr_atendimento_p
and	a.dt_atualizacao = (SELECT max(b.dt_atualizacao) from triagem_pronto_atend b where b.nr_atendimento = a.nr_atendimento);

if (nr_seq_classif_w IS NOT NULL AND nr_seq_classif_w::text <> '') then
	select	max(ds_classificacao),
			max(nr_seq_prioridade),
			max(ie_emergency_severity_score)
	into STRICT	ds_classificacao_w,
			nr_seq_prioridade_w,
			ie_emergency_severity_score_w
	from	triagem_classif_risco
	where	nr_sequencia	= nr_seq_classif_w;
end if;

if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then
	select	substr(obter_nome_pf(cd_pessoa_fisica),1,150) nm_medico,
			ds_codigo_prof
	into STRICT	nm_medico_triador_w,
			ds_registro_w
	from	pessoa_fisica
	where	cd_pessoa_fisica = cd_pessoa_fisica_w;
end if;


-- ie_opcao_p			varchar2 default 'C'
if (ie_opcao_p = 'C') then
	ds_retorno_w := ds_classificacao_w;
elsif (ie_opcao_p = 'R') then
	ds_retorno_w := ds_registro_w;
elsif (ie_opcao_p = 'M') then
	ds_retorno_w := nm_medico_triador_w;
elsif (ie_opcao_p = 'F') then
	ds_retorno_w := ds_funcao_w;
elsif (ie_opcao_p = 'FAB') then

	expressao1_w	:= wheb_mensagem_pck.get_Texto(1048120); --Medico
	expressao2_w	:= wheb_mensagem_pck.get_Texto(1048121);--Enfermeiro
	expressao3_w	:= wheb_mensagem_pck.get_Texto(1048122); --Tecnico de enfermagem
	expressao4_w	:= wheb_mensagem_pck.get_Texto(1048126); --Doutor
	expressao5_w	:= wheb_mensagem_pck.get_Texto(1048127);--Enf
	expressao6_w	:= wheb_mensagem_pck.get_Texto(1048128); --Tec Enf
	if (ds_funcao_w = expressao1_w) then
		ds_funcao_w := expressao4_w || ' ';
	elsif (ds_funcao_w = expressao2_w) then
		ds_funcao_w := expressao5_w || ' ';
	elsif (ds_funcao_w = expressao3_w) then
		ds_funcao_w := expressao6_w || ' ';
	end if;
	ds_retorno_w := ds_funcao_w;
elsif (ie_opcao_p = 'P') then
	ds_retorno_w := to_char(nr_seq_prioridade_w);
elsif (ie_opcao_p = 'S') then
	ds_retorno_w := to_char(ie_emergency_severity_score_w);
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_triagem_atendimento ( nr_atendimento_p bigint, ie_opcao_p text default 'C') FROM PUBLIC;

