CREATE OR REPLACE FUNCTION obter_tributo_item_cotacao (nr_item_cot_compra_p bigint, nr_seq_cot_item_forn_p bigint, cd_tributo_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE

retorno double precision;

BEGIN
	retorno := 0;
	IF ( ie_opcao_p = 'PR' ) THEN
		SELECT coalesce(MAX(pr_tributo),0)
		INTO STRICT	retorno
		FROM	cot_compra_forn_item_tr
		WHERE	nr_item_cot_compra 	= nr_item_cot_compra_p
		AND	nr_seq_cot_item_forn 	= nr_seq_cot_item_forn_p
		AND	cd_tributo		= cd_tributo_p;
	ELSE
		SELECT coalesce(MAX(vl_tributo),0)
		INTO STRICT	retorno
		FROM	cot_compra_forn_item_tr
		WHERE	nr_item_cot_compra 	= nr_item_cot_compra_p
		AND	nr_seq_cot_item_forn 	= nr_seq_cot_item_forn_p
		AND	cd_tributo		= cd_tributo_p;
	END IF;

	RETURN retorno;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
