CREATE OR REPLACE FUNCTION obter_tributo_titulo_pagar ( nr_seq_tributo_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

if (nr_seq_tributo_p IS NOT NULL AND nr_seq_tributo_p::text <> '') then
	if (ie_opcao_p = 0) then
		select ds_tributo
		into STRICT ds_retorno_w
		from	tributo b,
			titulo_pagar_imposto a
		where a.cd_tributo	= b.cd_tributo
		  and a.nr_sequencia = nr_seq_tributo_p;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
