SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_tributo_titulo ( nr_titulo_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


cd_tributo_w	bigint;
ds_retorno_w	varchar(255);


BEGIN

select	max(cd_tributo)
into STRICT	cd_tributo_w
from (SELECT	a.cd_tributo
	from	titulo_pagar a
	where	a.nr_titulo	= nr_titulo_p
	
union

	SELECT	b.cd_tributo
	from	titulo_pagar_imposto b,
		titulo_pagar a
	where	a.nr_titulo	= nr_titulo_p
	and	a.nr_seq_tributo	= b.nr_sequencia
	and	coalesce(a.cd_tributo::text, '') = '') alias2;

if (ie_opcao_p	= 'D') then
	select	a.ds_tributo
	into STRICT	ds_retorno_w
	from	tributo a
	where	a.cd_tributo	= cd_tributo_w;
else
	ds_retorno_w	:= cd_tributo_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
