CREATE OR REPLACE FUNCTION obter_trib_titulo_rec ( nr_titulo_p bigint, ie_tipo_tributo_p text) RETURNS bigint AS $body$
DECLARE


vl_tributo_w		double precision;


BEGIN

select	coalesce(sum(c.vl_tributo),0)
into STRICT	vl_tributo_w
from	tributo d,
	titulo_receber_trib c
where	c.nr_titulo		= nr_titulo_p
and	c.cd_tributo	= d.cd_tributo
and	d.ie_tipo_tributo	= ie_tipo_tributo_p;

return 	coalesce(vl_tributo_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
