CREATE OR REPLACE FUNCTION obter_trib_tit_rec_liq ( nr_titulo_p bigint, dt_referencia_p timestamp, ie_tipo_tributo_p text, ie_considerar_glosa_p text) RETURNS bigint AS $body$
DECLARE

 
/* 
ie_considerar_glosa_p 
'S' - Considerar 
'N' - NÃ£o considerar 
'G' - Somente sobre glosa 
 
*/
 
 
vl_tributo_baixa_w		double precision;


BEGIN 
 
select	/*+ USE_CONCAT */coalesce(sum(b.vl_baixa),0) 
into STRICT	vl_tributo_baixa_w 
from	tributo d, 
	titulo_receber_trib c, 
	titulo_receber_trib_baixa b, 
	titulo_receber_liq a 
where	a.nr_titulo	=	 nr_titulo_p 
and	a.nr_titulo		= b.nr_titulo 
and	a.nr_sequencia		= b.nr_seq_tit_liq 
and	b.nr_seq_tit_trib	= c.nr_sequencia 
and	c.cd_tributo		= d.cd_tributo 
and	d.ie_tipo_tributo	= ie_tipo_tributo_p 
and	((ie_considerar_glosa_p = 'S') or 
	 (a.vl_glosa = 0 AND ie_considerar_glosa_p = 'N') or 
	 (a.vl_glosa <> 0 AND ie_considerar_glosa_p = 'G')) 
and	a.dt_recebimento	<= fim_dia(dt_referencia_p);
 
return 	coalesce(vl_tributo_baixa_w,0);
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
