CREATE OR REPLACE FUNCTION obter_trocas_filt_dialisador ( nr_atendimento_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, nr_seq_mod_dialisador_p bigint) RETURNS varchar AS $body$
DECLARE


ds_evento_w	varchar(255);
dt_evento_w	varchar(70);

c01 CURSOR FOR
	SELECT	to_char(dt_evento,'dd/mm hh24:mi')
	from	hd_prescricao a,
		prescr_solucao b,
		prescr_medica c,
		hd_prescricao_evento d
	where	a.nr_prescricao   = c.nr_prescricao
	and	a.nr_sequencia    = b.nr_seq_dialise
	and	b.nr_prescricao   = c.nr_prescricao
	and	b.ie_status       <> 'V'
	and	c.nr_atendimento = nr_atendimento_p
	and	c.dt_inicio_prescr between dt_inicio_p and dt_fim_p
	and	(c.dt_liberacao IS NOT NULL AND c.dt_liberacao::text <> '')
	and	a.ie_tipo_dialise = 'H'
	and	a.nr_prescricao	  = d.nr_prescricao
	and	b.nr_seq_solucao  = d.nr_seq_solucao
	and	d.ie_evento	  = 'TF'
	and	a.nr_seq_mod_dialisador = nr_seq_mod_dialisador_p
	order by 1;

BEGIN

open c01;
loop
fetch c01 into
	dt_evento_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	if (ds_evento_w IS NOT NULL AND ds_evento_w::text <> '') then
		ds_evento_w := substr(ds_evento_w || chr(10),1,255);
	end if;

	ds_evento_w := substr(ds_evento_w || dt_evento_w || '  R',1,255);
	end;
end loop;
close c01;

return	ds_evento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
