CREATE OR REPLACE FUNCTION obter_troca_result_aval ( nr_seq_avaliacao_p bigint, nr_seq_item_p bigint) RETURNS varchar AS $body$
DECLARE


ds_resultado_w		varchar(30);
/*inverter resultado por 'X'  */

BEGIN

SELECT	CASE WHEN substr(obter_result_avaliacao(nr_seq_avaliacao_p, nr_seq_item_p),1,255)='S' THEN 'X' WHEN substr(obter_result_avaliacao(nr_seq_avaliacao_p, nr_seq_item_p),1,255)='N' THEN ' ' END
INTO STRICT	ds_resultado_w
;

return	ds_resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
