CREATE OR REPLACE FUNCTION obter_turno_cirurgia ( dt_cirurgia_p timestamp, cd_estabelecimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


dt_cirurgia_w		timestamp;
nr_seq_turno_w		bigint;
ds_turno_w		varchar(15);
dt_inicio_w		timestamp;
dt_final_w		timestamp;
ds_retorno_w		varchar(15);

c01 CURSOR FOR
	SELECT	nr_sequencia,
		ds_turno,
		to_date(to_char(clock_timestamp(),'dd/mm/yyyy')||' '||to_char(dt_inicial,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss'),
		to_date(to_char(clock_timestamp(),'dd/mm/yyyy')||' '||to_char(dt_final,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss')
	from	turno_cirurgia
	where	cd_estabelecimento	= cd_estabelecimento_p;


BEGIN

dt_cirurgia_w	:= to_date(to_char(clock_timestamp(),'dd/mm/yyyy')||' '||to_char(dt_cirurgia_p, 'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss');

open c01;
	loop
	fetch c01 into
		nr_seq_turno_w,
		ds_turno_w,
		dt_inicio_w,
		dt_final_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */

		if	(dt_cirurgia_w >= dt_inicio_w AND dt_cirurgia_w <= dt_final_w) or
			((dt_final_w < dt_inicio_w) and ((dt_cirurgia_w >= dt_inicio_w) or (dt_cirurgia_w <= dt_final_w))) then

			if (ie_opcao_p = 'C') then
				ds_retorno_w	:= nr_seq_turno_w;
			elsif (ie_opcao_p = 'D') then
				ds_retorno_w	:= ds_turno_w;
			end if;
		end if;

	end loop;
close c01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
