CREATE OR REPLACE FUNCTION obter_turno_encaixe_agecons (cd_agenda_p bigint, dt_agenda_p timestamp) RETURNS bigint AS $body$
DECLARE


vl_retorno_w	bigint;


BEGIN

select	max(nr_sequencia)
into STRICT	vl_retorno_w
from	agenda_turno
where	to_char(dt_agenda_p,'hh24:mi:ss') between to_char(hr_inicial,'hh24:mi:ss') and to_char(hr_final,'hh24:mi:ss')
and	((coalesce(dt_inicio_vigencia::text, '') = '') or (dt_inicio_vigencia <= trunc(dt_agenda_p)))
and	((coalesce(dt_final_vigencia::text, '') = '') or (dt_final_vigencia >= trunc(dt_agenda_p)))
and	nr_minuto_intervalo > 0
and	((ie_dia_semana = obter_cod_dia_semana(dt_agenda_p)) or ((ie_dia_semana = 9) and (obter_cod_dia_semana(dt_agenda_p) not in (7,1))))
and	cd_agenda = cd_agenda_p;


return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
