CREATE OR REPLACE FUNCTION obter_turno_esp_encaixe (cd_agenda_p bigint, dt_agenda_p timestamp) RETURNS bigint AS $body$
DECLARE


vl_retorno_w	bigint;
ie_dia_semana_w	smallint;

BEGIN

ie_dia_semana_w	:= obter_cod_dia_semana(dt_agenda_p);

select	max(nr_sequencia)
into STRICT	vl_retorno_w
from	agenda_turno_esp
where	to_char(dt_agenda_p,'hh24:mi:ss') between to_char(hr_inicial,'hh24:mi:ss') and to_char(hr_final,'hh24:mi:ss')
and	((coalesce(dt_agenda::text, '') = '') or (trunc(dt_agenda) <= trunc(dt_agenda_p)))
and	((coalesce(dt_agenda_fim::text, '') = '') or (trunc(dt_agenda_fim) >= trunc(dt_agenda_p)))
and	nr_minuto_intervalo > 0
and	((ie_dia_semana = ie_dia_semana_w) or ((ie_dia_semana = 9) and (ie_dia_Semana_w not in (7,1))) or (coalesce(ie_dia_semana::text, '') = ''))

and	cd_agenda = cd_agenda_p;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
