CREATE OR REPLACE FUNCTION obter_tuss_brasindice ( cd_apresentacao_p text, cd_laboratorio_p text, cd_medicamento_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(15);
qt_registro_w		integer;
cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type;


BEGIN
cd_estabelecimento_w := obter_estabelecimento_ativo;

begin
select	1
into STRICT	qt_registro_w
from	brasindice_preco
where	cd_medicamento		= cd_medicamento_p
and	cd_laboratorio		= cd_laboratorio_p
and	cd_apresentacao		= cd_apresentacao_p
and	cd_estabelecimento	= cd_estabelecimento_w;
exception
when others then
	qt_registro_w := 0;
end;

if (qt_registro_w = 0) then
	select  coalesce(max(cd_tuss),0)
	into STRICT    ds_retorno_w
	from    brasindice_preco
	where   cd_medicamento  = cd_medicamento_p
	and     cd_laboratorio  = cd_laboratorio_p
	and     cd_apresentacao = cd_apresentacao_p
	and	dt_inicio_vigencia	= (	SELECT	max(dt_inicio_vigencia)
						from	brasindice_preco
						where	cd_medicamento		= cd_medicamento_p
						and	cd_laboratorio		= cd_laboratorio_p
						and	cd_apresentacao		= cd_apresentacao_p);
else
	select  coalesce(max(cd_tuss),0)
	into STRICT    ds_retorno_w
	from    brasindice_preco
	where   cd_medicamento  = cd_medicamento_p
	and     cd_laboratorio  = cd_laboratorio_p
	and     cd_apresentacao = cd_apresentacao_p
	and	coalesce(cd_estabelecimento, cd_estabelecimento_w)	= cd_estabelecimento_w
	and	dt_inicio_vigencia	= (	SELECT	max(dt_inicio_vigencia)
						from	brasindice_preco
						where	cd_medicamento		= cd_medicamento_p
						and	cd_laboratorio		= cd_laboratorio_p
						and	cd_apresentacao		= cd_apresentacao_p
						and	coalesce(cd_estabelecimento, cd_estabelecimento_w)	= cd_estabelecimento_w);
end if;

return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
