SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_tuss_simpro_preco (nr_seq_simpro_p bigint, dt_vigencia_p timestamp) RETURNS bigint AS $body$
DECLARE


cd_simpro_w	material_simpro.cd_simpro%type;
cd_tuss_w	simpro_preco.cd_tuss%type;
cd_estab_simpro_w	estabelecimento.cd_estabelecimento%type;


BEGIN

cd_estab_simpro_w := wheb_usuario_pck.get_cd_estabelecimento;

select	coalesce(max(cd_simpro),0)
into STRICT	cd_simpro_w
from	material_simpro
where	nr_sequencia			= nr_seq_simpro_p
and	coalesce(dt_vigencia, dt_vigencia_p) <= dt_vigencia_p;

if (cd_simpro_w > 0) then
	select	max(cd_tuss)
	into STRICT	cd_tuss_w
	from	simpro_preco
	where	cd_simpro	= cd_simpro_w
	and	coalesce(cd_estabelecimento, coalesce(cd_estab_simpro_w, 0)) = coalesce(cd_estab_simpro_w, 0)
	and	dt_vigencia 	=
				(SELECT max(dt_vigencia)
				from 	simpro_preco
				where 	cd_simpro = cd_simpro_w
				and	coalesce(cd_estabelecimento, coalesce(cd_estab_simpro_w, 0)) = coalesce(cd_estab_simpro_w, 0)
				and 	dt_vigencia <= dt_vigencia_p);
end if;

return 	cd_tuss_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
