SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_txid_pix (NR_SEQUENCIA_P bigint) RETURNS varchar AS $body$
DECLARE


DS_RETORNO_W	varchar(250);
DS_TXID_W varchar(255);


BEGIN

IF (NR_SEQUENCIA_P IS NOT NULL AND NR_SEQUENCIA_P::text <> '') THEN

    SELECT DS_TXID
    INTO STRICT DS_TXID_W
    FROM PIX_COBRANCA
    WHERE NR_SEQUENCIA = NR_SEQUENCIA_P;

    IF coalesce(DS_TXID_W::text, '') = '' THEN

        SELECT DBMS_RANDOM.STRING('A', 35) STR
        INTO STRICT DS_RETORNO_W
;

	ELSE  DS_RETORNO_W := DS_TXID_W;

    END IF;

ELSE
  SELECT DBMS_RANDOM.STRING('A', 35) STR
        INTO STRICT DS_RETORNO_W
;

END IF;

RETURN	DS_RETORNO_W;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
